update redenvelope_rule
ACTIVITY_CODE = #{activityCode}, RULE_NAME = #{ruleName}, TOTAL_NUM = #{totalNum}, UPDATE_TIME = #{updateTime}, DELETE_FLAG = #{deleteFlag}, GRANT_TYPE = #{grantType}, GRANT_MAX = #{grantMax}, GRANT_MIN = #{grantMin}, GRANT_RATE = #{grantRate}, grant_project_type = #{grantProjectType}, GRANT_URL = #{grantUrl}, GRANT_START_TIME = #{grantStartTime}, GRANT_END_TIME = #{grantEndTime}, USE_PROJECT_TYPE = #{useProjectType}, USE_VALID_DAY = #{useValidDay}, USE_EXPIRE_TIME = #{useExpireTime},
uuid = #{uuid}

update redenvelope_rule set lssue_num=lssue_num+1 
where uuid= #{uuid}
  and IFNULL(total_num,lssue_num+1) >= (lssue_num+1)

mapper.select:
select count(*) from redenvelope_rule where uuid in
#{id}
and status=1
