mapper.insert:
BEGIN
insert into project_repayment( ) values ( #{item.uuid}, #{item.userId}, #{item.projectId}, #{item.investId}, #{item.repayTime}, #{item.realRepayTime}, #{item.payment}, #{item.payedAmount}, #{item.capital}, #{item.interest}, #{item.status}, #{item.repayType}, #{item.period}, #{item.periods}, #{item.lateDays}, #{item.lateInterest}, #{item.merchantLateInterest}, #{item.repayUserId}, #{item.repayTradeNo}, #{item.createTime} )
; END ;

mapper.update:
update project_repayment
REAL_REPAY_TIME = #{realRepayTime}, PAYED_AMOUNT = #{payedAmount}, STATUS = #{status}, REPAY_TYPE = #{repayType}, LATE_DAYS = #{lateDays}, LATE_INTEREST = #{lateInterest}, MERCHANT_LATE_INTEREST = #{merchantLateInterest}, REPAY_USER_ID = #{repayUserId}, REPAY_TRADE_NO = #{repayTradeNo}
uuid = #{uuid}

BEGIN
update project_repayment
LATE_DAYS = #{item.lateDays}, LATE_INTEREST = #{item.lateInterest}, MERCHANT_LATE_INTEREST = #{item.merchantLateInterest},
where uuid = #{item.uuid}
; END ;

mapper.select:
select nvl(sum(pr.payment - pr.payed_amount + pr.late_interest + pr.merchant_late_interest), 0)
from project_repayment pr,project p 
where pr.project_id = p.uuid and p.realize_flag = '0' and  pr.status = 0 and pr.user_id = #{userId}

SELECT * FROM (
	select ROW_NUMBER() OVER(ORDER BY pr.repay_time asc) RN, pr.repay_time,nvl(pr.payment - pr.payed_amount + pr.late_interest + pr.merchant_late_interest, 0) as payment
	from project_repayment pr,project p 
	where pr.project_id = p.uuid and p.realize_flag = '0' and pr.status = 0 and pr.user_id = #{userId}
) WHERE RN = 1

  	select  count(uuid)
	from project_repayment
	where status = 0 
	    and project_id = #{projectId}  
	   and period < #{period} 
	   and invest_id = #{investId} 
	  and repay_time = #{repayTime}

        select  pr.uuid,pr.project_id,p.project_name,pr.period,p.time_limit,pr.capital,pr.interest,pr.late_interest late_interest,pr.repay_time,pr.real_repay_time,
        (case when pr.status='0'  and pr.invest_id is  null and p.next_repay_time != pr.repay_time then '-1' else pr.status end) status,pr.late_days,pr.repay_type,pr.periods,pr.MERCHANT_LATE_INTEREST
         from project_repayment pr, project p where pr.project_id=p.uuid
         and p.realize_flag = '0'
        and pr.user_id = #{userId}





                    and (pr.status = #{status} or pr.repay_type = #{repayTypeAdvance})



	                  and (pr.late_days is null or pr.late_days='') and pr.status = #{status}



	                  and (pr.late_days is not null and pr.late_days > 0) and pr.status = #{status}



	                  and pr.repay_type = #{repayTypeAdvance}

                and #{dateTypeTime} > pr.repay_time

			     and pr.repay_time >= to_date(#{startTime},'yyyy-mm-dd hh24:mi:ss')
			     and pr.repay_time <= to_date(#{endTime},'yyyy-mm-dd hh24:mi:ss')       





	             and pr.status = #{status} and (pr.repay_type is null or pr.repay_type != #{repayTypeAdvance})
	             and pr.real_repay_time > #{dateTypeTime}

				     and pr.real_repay_time >= to_date(#{startTime},'yyyy-mm-dd hh24:mi:ss')
				     and pr.real_repay_time <= to_date(#{endTime},'yyyy-mm-dd hh24:mi:ss')        



        order by p.project_no desc, pr.repay_time asc
         order by ${page.sort} ${page.order}

     select  max( real_repay_time )  last_repay_time
	  from project_repayment
	where project_id=#{projectId}

select repay_time from(
	SELECT ROW_NUMBER() OVER(order by repay_time asc) RN, repay_time
	from project_repayment
	where project_id =#{projectId}
	and repay_time > #{repayTime}
	and status =0
) WHERE RN = 1

select u.user_name, u.real_name real_name, p.project_name,p.status projectStatus,p.overdue_fee_rate,u.mobile, t.type_name project_type_name, p.project_no,pr.uuid, pr.user_id, pr.project_id, pr.invest_id, pr.repay_time, pr.real_repay_time, pr.payment, pr.payed_amount, pr.capital, pr.interest, pr.status, pr.repay_type, pr.period, pr.periods, pr.late_days, pr.late_interest, pr.repay_user_id, pr.repay_trade_no, pr.create_time,pr.MERCHANT_LATE_INTEREST
,( SELECT count(1) from urge_repay_log where repayment_id=pr.uuid) URGE_REPAY_COUNT
from project_repayment pr left join project p on pr.project_id = p.uuid inner join project_type t on p.project_type_id =t.uuid left join users u on pr.user_id = u.uuid where p.is_vouch=1 and pr.repay_type in (2,3)
and (p.PROJECT_NAME like '%'||#{keywords}||'%' or u.USER_NAME like '%'||#{keywords}||'%' or u.real_name like '%'||#{keywords}||'%' )
and u.user_name like '%'|| #{userName}||'%' and u.real_name like '%'||#{realName}||'%' and pr.REPAY_TIME >= to_date(#{startTime},'yyyy-mm-dd hh24:mi:ss') and pr.REPAY_TIME <= to_date(#{endTime},'yyyy-mm-dd hh24:mi:ss')
order by ${page.sort} ${page.order}
