mapper.insert:
BEGIN
insert into realize( ) values ( #{item.projectId}, #{item.investId}, #{item.oldProjectId}, #{item.originalProjectId}, #{item.deadline}, #{item.ruleId}, #{item.realizeAmount}, #{item.realizeInterest} )
; END ;

mapper.update:
		update realize set delete_flag = 1
		where project_id in

            #{id}

update realize
INVEST_ID = #{investId}, OLD_PROJECT_ID = #{oldProjectId}, ORIGINAL_PROJECT_ID = #{originalProjectId}, DEADLINE = #{deadline}, RULE_ID = #{ruleId}, REALIZE_AMOUNT = #{realizeAmount}, REALIZE_INTEREST = #{realizeInterest},
project_id = #{projectId}

mapper.select:
       	select p.uuid,p.user_id,p.project_no,p.project_name,p.raise_account,p.account,p.account_yes,p.apr,p.add_apr,p.interest_style,p.interest_start_days,
		p.repay_style,p.fixed_repay_day,p.time_type,p.time_limit,p.create_time,p.publish_verify_time,p.update_time,p.show_time,p.stop_time,p.sale_time,
		p.vouch_verify_time,p.raise_time_limit,p.raise_end_time,p.review_time,p.borrow_manage_rate,p.interest_manage_rate,p.overdue_fee_rate,p.novice,p.choice,
		p.sale_style,p.lowest_account,p.most_account,p.step_account,p.total_copies,p.copy_account,p.lowest_copies,p.most_copies,p.risk_level,p.red_envelope_useful,
		p.red_envelope_rate,p.additional_rate_useful,p.project_type_id,p.status,p.is_vouch,p.vouch_id,p.vouch_status,p.interest_time,p.borrow_flag,p.realize_flag,
		p.sale_channel,p.specific_sale,p.specific_sale_rule,p.repay_amount,p.repay_yes_account,p.repay_yes_interest,p.total_period,p.repayed_period,p.next_repay_time,
		p.last_repay_time,p.real_last_repay_time,p.redenvelope_rule_id,p.rate_coupon_rule_id,p.protocol_id,
		r.project_id,r.invest_id,r.old_project_id,r.original_project_id,r.deadline,r.rule_id,r.realize_amount,r.realize_interest
		from realize r left join project p on r.project_id = p.uuid 

	        and r.project_id = #{projectId}
	        and p.user_id = #{userId}
	        and p.status = #{status}
	        and p.status in 
				#{status}

	        and p.project_name like '%'|| #{projectName}||'%'
	        and p.create_time > #{dateTypeTime}


			     and p.create_time >= to_date(#{startTime},'yyyy-mm-dd hh24:mi:ss')
			     and p.create_time <= to_date(#{endTime},'yyyy-mm-dd hh24:mi:ss')        



	        	order by ${page.sort} ${page.order}

        order by p.CREATE_TIME desc

select p.uuid,p.user_id,p.project_no,p.project_name,p.raise_account,p.account,p.account_yes,p.apr,p.add_apr,p.interest_style,p.interest_start_days, p.repay_style,p.time_type,p.time_limit,p.publish_verify_time,p.update_time,p.show_time,p.stop_time,p.sale_time,p.raise_time_limit,p.raise_end_time, p.review_time,p.novice,p.choice,p.sale_style,p.lowest_account,p.most_account,p.risk_level,p.project_type_id,p.status,p.is_vouch,p.vouch_id, p.vouch_status,p.interest_time,p.borrow_flag,p.realize_flag,p.sale_channel,p.specific_sale,p.specific_sale_rule,p.repay_amount,p.repay_yes_account, p.repay_yes_interest,p.total_period,p.repayed_period,p.next_repay_time,p.last_repay_time,p.real_last_repay_time, r.project_id,r.invest_id,r.old_project_id,r.original_project_id,r.deadline,r.rule_id from realize r left join project p on r.project_id = p.uuid
and r.project_id = #{projectId} and p.user_id = #{userId} and p.status = #{status} and p.status in #{status}
and p.project_name like '%'|| #{projectName}||'%'
order by ${page.sort} ${page.order}
order by (case p.account when p.account>p.account_yes then 1 else 0 end ) desc, p.status asc,p.show_time desc,p.sale_time asc
