mapper.insert:
BEGIN
insert into sys_operator( ) values ( #{item.uuid}, #{item.loginName}, #{item.pwd}, #{item.realName}, #{item.empNo}, #{item.orgId}, #{item.telephone}, #{item.mobile}, #{item.email}, #{item.qq}, #{item.createTime}, #{item.status}, #{item.deleteFlag}, #{item.remark} )
; END ;

mapper.update:
		update sys_operator set delete_flag = 1
		where uuid in

            #{id}

update sys_operator
LOGIN_NAME = #{loginName}, PWD = #{pwd}, REAL_NAME = #{realName}, EMP_NO = #{empNo}, ORG_ID = #{orgId}, TELEPHONE = #{telephone}, MOBILE = #{mobile}, EMAIL = #{email}, QQ = #{qq}, CREATE_TIME = #{createTime}, STATUS = #{status}, DELETE_FLAG = #{deleteFlag}, REMARK = #{remark},
uuid = #{uuid}

mapper.select:
select r.code
 from sys_role r,
 	 sys_operator_role ur 
 where ur.role_id = r.uuid 
 	and ur.OPERATOR_ID = #{value}

    	select count(uuid) from sys_operator 
    	where org_id in

	            #{id}
